import "./App.css";
import Products from "./components/Products.js";
import TabPanel from "./components/TabPanel";

import { useState } from "react";

import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
// import TabPanel from "@mui/material/TabPanel";

//import material-ui search bar
// import Button from "@mui/material/Button";
// import Autocomplete from "@material-ui/lab/Autocomplete";

function App() {
  const [value, setValue] = useState(0);
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const url = "https://api.punkapi.com/v2/beers?page=1&per_page=80";
  const { data, loading, error } = useFetch(url);

  return (
    <div className="App">
      {/* <Products /> */}
      <Tabs value={value} onChange={handleChange} centered>
        <Tab label="all available beers" />
        <Tab label="pair with pizza" />
        <Tab label="pair with steak" />
      </Tabs>
      <TabPanel value={{ value, data, loading, error }} index={0} />
      <TabPanel value={{ value, data, loading, error }} index={1} />
      <TabPanel value={{ value, data, loading, error }} index={2} />
    </div>
  );
}

export default App;
