import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { useState, useEffect } from "react";

export default function TabPanel(props) {
  const value = props.value;
  const index = props.index;
  const { data, loading, error } = props.data;
  const [filteredData, setFilteredData] = useState();
  //   console.log(props);
  const filterData = (e) => {
    if (e.target.value === "a-z") {
      const arr = [...data];
      if (value === index && index === 2) {
        console.log("a-z steak");
        let a = arr.filter((product) => {
          console.log(product.food_pairing.join("").includes("steak"));
          return product.food_pairing.join("").includes("steak");
        });
        console.log(a);
        arr.sort((a, b) => a.name.localeCompare(b.name));

        setFilteredData(arr);
      } else {
        arr.sort((a, b) => a.name.localeCompare(b.name));
        setFilteredData(arr);
      }
    }
    if (e.target.value === "z-a") {
      const arr = [...data];
      arr.sort((a, b) => b.name.localeCompare(a.name));
      setFilteredData(arr);
    }
    if (e.target.value === "low-high") {
      const arr = [...data];
      arr.sort((a, b) => a.abv - b.abv);
      setFilteredData(arr);
    }
    if (e.target.value === "high-low") {
      const arr = [...data];
      arr.sort((a, b) => b.abv - a.abv);
      setFilteredData(arr);
    }
  };

  useEffect(() => {
    setFilteredData(data);
  }, [data]);

  //   useEffect(() => {
  //     console.log(value, index);
  //     if (value === index && index === 1) {
  //       // filter products  where array food_pairing contains "pizza"
  //       console.log(index);
  //       setFilteredData(
  //         data.filter((product) =>
  //           product.food_pairing.join("").includes("pizza")
  //         )
  //       );
  //     }
  //     if (value === index && index === 2) {
  //       // filter products  where array food_pairing contains "steak"
  //       console.log(index);
  //       setFilteredData(
  //         data.filter((product) =>
  //           product.food_pairing.join("").includes("steak")
  //         )
  //       );
  //     }
  //   }, [value, index]);

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      //   {...other}
    >
      <select
        style={{ position: "sticky", top: "0", zIndex: "1" }}
        onChange={filterData}
      >
        <option value="">All</option>
        <option value="a-z">A-Z</option>
        <option value="z-a">Z-A</option>
        <option value="low-high">ABV Low-High</option>
        <option value="high-low">ABV High-Low</option>
      </select>

      {value === index && (
        <Box sx={{ p: 3 }}>
          {loading && <p>Loading...</p>}
          {error && <p>Error: {error.message}</p>}
          {filteredData &&
            filteredData.map((product) => (
              <p key={product.id}>{product.name}</p>
              //<Product key={product.id} product={product} />
            ))}
          {/* <Typography>children</Typography> */}
        </Box>
      )}
    </div>
  );
}
