import React from "react";
import Products from "../components/Products.js";
import TabPanel from "../components/TabPanel";

import { useState, useEffect } from "react";

import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import useFetch from "./hooks/useFetch";

export default function Home() {
  const url = "https://api.punkapi.com/v2/beers?page=1&per_page=80";

  const [value, setValue] = useState(0);
  const [pizzaData, setPizzaData] = useState([]);
  const [steakData, setSteakData] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const { data, loading, error } = useFetch(url);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  useEffect(() => {
    if (data) {
      setSteakData(
        data.filter((product) =>
          product.food_pairing.join("").includes("steak")
        )
      );
      setPizzaData(
        data.filter((product) =>
          product.food_pairing.join("").includes("pizza")
        )
      );
      setAllProducts(data.sort((a, b) => a.name.localeCompare(b.name)));
    }
  }, [data]);

  return (
    <div>
      <Tabs value={value} onChange={handleChange} centered>
        <Tab label="all available beers" />
        <Tab label="pair with pizza" />
        <Tab label="pair with steak" />
      </Tabs>
      <TabPanel value={value} data={{ data, loading, error }} index={0} />
      <TabPanel
        value={value}
        data={{ data: pizzaData, loading, error }}
        index={1}
      />
      <TabPanel
        value={value}
        data={{ data: steakData, loading, error }}
        index={2}
      />
    </div>
  );
}
